function addScript(source){var script=document.createElement('script')
script.src=source
script.charset='UTF-8'
document.getElementsByTagName('head')[0].appendChild(script)}
function addStyle(source){var link=document.createElement('link')
link.rel='stylesheet'
link.href=source
document.getElementsByTagName('head')[0].appendChild(link)}
function loadAppFromCdn(path){var request=new XMLHttpRequest()
request.onload=onload
request.open('GET',path+'/'+'manifest.json?'+new Date().getTime(),true)
request.send()
function onload(){var data=JSON.parse(request.response)
var keys=Object.keys(data)
for(var i in keys){var key=keys[i]
try{if(key.indexOf('main.js')!==-1){addScript(path+data[key])}
if(key.indexOf('.css')!==-1){addStyle(path+data[key])}}catch(e){}}}}
function getPubPath(){return window.pubPath.replace(/version(\d*)/,'')}
if(localStorage.getItem('reactDev')==='true'){addScript('https://cdn-dev:8090/main.js')}else{loadAppFromCdn(getPubPath())}